import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as n,c as s,a as o,b as r,e,d as l,r as i}from"./app.95c6795b.js";const c={},d=e("\u76F8\u5173\u8D44\u6599\uFF1A"),h={href:"https://pmthinking.notion.site/2e0e6eb147b44bd28b89decd33093df3",target:"_blank",rel:"noopener noreferrer"},u=e("\u4EC0\u4E48\u662F\u6697\u77E5\u8BC6"),b=e("\u3001"),f={href:"https://mp.weixin.qq.com/s/laGz0C5V8bdFyzTqTs618A",target:"_blank",rel:"noopener noreferrer"},_=e("\u6697\u77E5\u8BC6\uFF1A\u201C\u542C\u8FC7\u5F88\u591A\u9053\u7406\uFF0C\u5374\u4F9D\u7136\u8FC7\u4E0D\u597D\u8FD9\u4E00\u751F\u201D\u7684\u79D8\u5BC6"),p=l('<h2 id="\u524D\u8A00" tabindex="-1"><a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a> \u524D\u8A00</h2><blockquote><p>\u4E3A\u4EC0\u4E48\u4E0D\u80FD\u7528\u5E73\u7B49\u7684\u76EE\u5149\u770B\u5F85\u6BCF\u4E00\u4E2A\u4EBA\uFF1F\u201C\u77E5\u6613\u884C\u96BE\u201D\u80CC\u540E\u6DF1\u5C42\u6B21\u7684\u539F\u56E0\u662F\u4EC0\u4E48\uFF1F\u201C\u8FD0\u6C14\u201D\u80CC\u540E\u5230\u5E95\u88AB\u4EC0\u4E48\u4E3B\u5BFC\uFF1F\u4E3A\u4EC0\u4E48\u4E0D\u80FD\u62FF\u81EA\u5DF1\u7684\u7231\u597D\u53BB\u6311\u6218\u522B\u4EBA\u7684\u996D\u7897\uFF1F\u2026\u2026<br><br id="Story"> \u539F\u6765\u8FD9\u4E9B\u95EE\u9898\u80CC\u540E\u90FD\u6709\u4E00\u4E2A\u5171\u540C\u7684\u4E3B\u8C0B\uFF0C\u5C31\u662F\u88AB\u6211\u4EEC\u5FFD\u7565\u7684\u6697\u77E5\u8BC6\u3002\u53EA\u6709\u610F\u8BC6\u5230<strong>\u6697\u77E5\u8BC6\u7684\u91CD\u8981\u6027</strong>\uFF0C\u6211\u4EEC\u624D\u4F1A\u8D70\u51FA\u81EA\u5DF1\u7684\u8212\u9002\u533A\u3001\u624D\u4F1A\u660E\u767D\u62FC\u56FE\u7684\u91CD\u8981\u6027\uFF0C\u624D\u4F1A\u57F9\u517B\u523B\u610F\u601D\u8003\u7684\u4E60\u60EF\uFF0C\u624D\u4F1A\u660E\u767D\u5355\u7EAF\u8BF4\u6559\u65E0\u7528\u7684\u539F\u56E0\uFF0C\u624D\u4F1A\u7406\u89E3\u4E07\u7269\u76F8\u4F9D\uFF0C\u56E0\u7F18\u6240\u751F\u3002<br><br> \u6709\u8FD9\u6837\u4E00\u4E2A\u6545\u4E8B\uFF1A<br> \u53E4\u65F6\u5019\uFF0C\u6709\u4E00\u6237\u4EBA\u5BB6\u7684\u7530\u88AB\u5C71\u4E0A\u7684\u843D\u77F3\u6321\u4F4F\u4E86\uFF0C\u65E0\u6CD5\u8015\u79CD\u3002\u843D\u77F3\u5F88\u5927\uFF0C\u65E0\u6CD5\u79FB\u8D70\uFF0C\u53EA\u80FD\u7528\u706B\u836F\u70B8\u5F00\u3002\u4E8E\u662F\uFF0C\u8FD9\u6237\u4EBA\u5BB6\u5C31\u53BB\u5BFB\u627E\u706B\u836F\u7684\u914D\u65B9\u6765\u5236\u4F5C\u706B\u836F\u3002\u4ED6\u4EEC\u6253\u542C\u4E86\u5F88\u4E45\uFF0C\u7EC8\u4E8E\u5F97\u77E5\u706B\u836F\u662F\u7531\u786B\u3001\u785D\u6DF7\u5408\u800C\u6210\u7684\u3002\u4E8E\u662F\u4ED6\u4EEC\u5C31\u4E70\u6765\u4E86\u786B\uFF0C\u785D\uFF0C\u6DF7\u5408\u5728\u4E00\u8D77\uFF0C\u4F46\u662F\u4ED6\u4EEC\u70B9\u71C3\u540E\uFF0C\u706B\u836F\u5374\u6CA1\u6709\u7206\u70B8\u3002\u524D\u524D\u540E\u540E\u5C1D\u8BD5\u4E86\u5341\u51E0\u6B21\u90FD\u6CA1\u6709\u6210\u529F\uFF0C\u4ED6\u4EEC\u5F88\u751F\u6C14\uFF0C\u540C\u65F6\u4E5F\u82B1\u4E86\u4E0D\u5C11\u94B1\uFF0C\u6700\u540E\u65E0\u529B\u6298\u817E\uFF0C\u53EA\u597D\u653E\u5F03\u3002 \u5982\u679C\u8981\u5236\u4F5C\u6210\u706B\u836F\uFF0C\u786B\u548C\u785D\u7684\u6BD4\u4F8B\u8981\u8FBE\u52301:3\u624D\u884C\u3002\u5C3D\u7BA1\u8FD9\u6237\u4E0D\u5E78\u7684\u4EBA\u5BB6\u5DF2\u7ECF\u77E5\u9053\u4E86\u706B\u836F\u7684\u7EC4\u6210\uFF0C\u4F46\u662F\u56E0\u4E3A\u7F3A\u5C11\u4E86\u5173\u4E8E\u7EC4\u6210\u5143\u7D20\u7684\u5B9A\u91CF\u77E5\u8BC6\uFF0C\u800C\u4EE5\u5931\u8D25\u544A\u7EC8\u3002<br><br> \u8FD9\u4E2A\u6545\u4E8B\u6709\u8BB8\u591A\u5F53\u4EE3\u7684\u7248\u672C\uFF0C\u50CF\u5B66\u4E60\u82F1\u8BED\uFF0C\u50CF\u5F00\u5E97\u5356\u4E1C\u897F\u5C31\u662F\u5982\u6B64\u3002\u6211\u4EEC\u80FD\u4ECE\u5404\u7C7B\u5730\u65B9\uFF0C\u8BF8\u5982\u5FAE\u4FE1\u63A8\u6587\uFF0C\u77E5\u4E4E\u56DE\u7B54\uFF0C\u901A\u4FD7\u4E66\u7C4D\uFF0C\u4E13\u4E1A\u8BBA\u6587\uFF0C\u540D\u4EBA\u8BB2\u5EA7\uFF0C\u58F0\u60C5\u5E76\u8302\u7684\u6F14\u8BB2\uFF0C\u5A13\u5A13\u9053\u6765\u7684\u5206\u4EAB\u4E2D\u542C\u5230\u5404\u79CD\u706B\u836F\u914D\u65B9\uFF0C\u4F46\u662F\u8FD9\u6837\u7684\u914D\u65B9\u5E38\u5E38\u662F\u4E0D\u5B8C\u6574\u7684\uFF0C\u7F3A\u5931\u5173\u952E\u7684\u77E5\u8BC6\u3002<br><br> \u800C\u8FD9\u6837\u7684\u77E5\u8BC6\uFF0C\u5C31\u662F\u672C\u6587\u8981\u5173\u6CE8\u7684\u2014\u2014\u6697\u77E5\u8BC6\u3002\u6697\u77E5\u8BC6\u662F\u88AB\u5FFD\u7565\u7684\u77E5\u8BC6\u7C7B\u578B\uFF0C\u4F46\u5B83\u662F\u5982\u6B64\u7684\u5173\u952E\u3002\u5C31\u50CF\u6211\u4EEC\u4F53\u5185\u7684\u5FAE\u91CF\u5143\u7D20\uFF0C\u5B58\u5728\u91CF\u6781\u5C11\uFF0C\u662F\u6211\u4EEC\u4F53\u91CD\u7684\u4E07\u5206\u4E4B\u4E00\uFF0C\u5374\u5FC5\u4E0D\u53EF\u5C11\uFF0C\u4E3E\u4F8B\u800C\u8A00\uFF0C\u513F\u7AE5\u7F3A\u950C\u4F1A\u5BFC\u81F4\u5927\u8111\u53D1\u80B2\u53D7\u635F\uFF0C\u8BB0\u5FC6\u529B\u667A\u529B\u4E0B\u964D\uFF0C\u60C5\u7EEA\u5931\u63A7\uFF0C\u5FC3\u7406\u7D20\u8D28\u5DEE\u7B49\u72B6\u51B5\u3002</p></blockquote><h2 id="\u6697\u77E5\u8BC6\u7684\u4E09\u79CD\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#\u6697\u77E5\u8BC6\u7684\u4E09\u79CD\u7C7B\u578B" aria-hidden="true">#</a> \u6697\u77E5\u8BC6\u7684\u4E09\u79CD\u7C7B\u578B</h2><ul><li>\u5206\u6563\u77E5\u8BC6\uFF1B</li><li>\u7528\u4E8E\u5B9A\u91CF\u7684\u91CD\u8981\u6027\u77E5\u8BC6\uFF1B</li><li>\u672A\u5728\u73B0\u6709\u5B66\u79D1\u6846\u67B6\u5185\u7684\u5B9E\u8DF5\u77E5\u8BC6\u3002</li></ul><h3 id="_1-\u54C8\u8036\u514B\u7684\u5206\u6563\u77E5\u8BC6" tabindex="-1"><a class="header-anchor" href="#_1-\u54C8\u8036\u514B\u7684\u5206\u6563\u77E5\u8BC6" aria-hidden="true">#</a> 1. \u54C8\u8036\u514B\u7684\u5206\u6563\u77E5\u8BC6</h3><ul><li>\u54C8\u8036\u514B\uFF08F. A. Hayek\uFF09\uFF1A1974\u5E74\u8BFA\u8D1D\u5C14\u7ECF\u6D4E\u5B66\u5956\u5F97\u4E3B\uFF0C<br> \u5728\u300A\u77E5\u8BC6\u5728\u793E\u4F1A\u4E2D\u7684\u8FD0\u7528\uFF08The Usage of Knowledge in Society\uFF09\u300B<sup class="footnote-ref"><a href="#footnote1">[1]</a><a class="footnote-anchor" id="footnote-ref1"></a></sup>\u4E2D\u63D0\u51FA\u4E86<strong>\u5206\u6563\u77E5\u8BC6</strong>\u7684\u6982\u5FF5\u3002</li><li>\u4EC0\u4E48\u662F\u5206\u6563\u77E5\u8BC6\uFF1A <blockquote><p>\u201C\u4ECA\u5929\uFF0C\u8C01\u8981\u662F\u8BA4\u4E3A\u79D1\u5B66\u77E5\u8BC6\u4E0D\u662F\u5168\u90E8\u77E5\u8BC6\u7684\u6982\u62EC\uFF0C\u7B80\u76F4\u5C31\u662F\u5F02\u7AEF\u90AA\u8BF4\u3002\u4F46\u662F\u7A0D\u52A0\u601D\u7D22\u5C31\u4F1A\u77E5\u9053\uFF0C\u5F53\u7136\u8FD8\u5B58\u5728\u8BB8\u591A\u975E\u5E38\u91CD\u8981\u4F46\u672A\u7EC4\u7EC7\u8D77\u6765\u7684\u77E5\u8BC6\uFF0C\u5373\u6709\u5173<strong>\u7279\u5B9A\u65F6\u95F4\u548C\u5730\u70B9</strong>\u7684\u77E5\u8BC6\uFF0C\u5B83\u4EEC\u5728\u4E00\u822C\u610F\u4E49\u4E0A\u751A\u81F3\u4E0D\u53EF\u80FD\u79F0\u4E3A\u79D1\u5B66\u7684\u77E5\u8BC6\uFF0C \u4F46\u6B63\u662F\u5728\u8FD9\u65B9\u9762\uFF0C\u6BCF\u4E2A\u4EBA\u5B9E\u9645\u4E0A\u90FD\u5BF9\u6240\u6709\u5176\u4ED6\u4EBA\u6765\u8BF4\u5177\u6709\u67D0\u79CD\u4F18\u52BF\uFF0C\u56E0\u4E3A<span style="color:#FF8C00;"><u>\u6BCF\u4E2A\u4EBA\u90FD\u638C\u63E1\u53EF\u4EE5\u5229\u7528\u7684\u72EC\u4E00\u65E0\u4E8C\u7684\u4FE1\u606F\uFF0C\u800C\u57FA\u4E8E\u8FD9\u79CD\u4FE1\u606F\u7684\u51B3\u7B56\u53EA\u6709\u7531\u6BCF\u4E2A\u4E2A\u4EBA\u4F5C\u51FA\uFF0C\u6216\u7531\u4ED6\u79EF\u6781\u53C2\u4E0E\u4F5C\u51FA\uFF0C\u8FD9\u79CD\u4FE1\u606F\u624D\u80FD\u88AB\u5229\u7528</u></span>\u3002\u201D\u2014\u2014\u54C8\u8036\u514B</p></blockquote><ul><li>\u8FD9\u662F\u4E00\u79CD<strong>\u975E\u5B66\u79D1\u6027\u3001\u672A\u7ECF\u7EC4\u7EC7\u3001\u6CA1\u6709\u7CFB\u7EDF\u5316\u3001\u5206\u6563\u4E8E\u6BCF\u4E2A\u4E2A\u4F53\u7684\u77E5\u8BC6</strong>\u3002<br> \u4F8B\u5982\u4F60\u77E5\u9053\u81EA\u5DF1\u793E\u533A\u5468\u56F4\u7F3A\u5C11\u4E00\u4E2A\u6C34\u679C\u5E97\uFF0C\u8FD9\u5C31\u662F\u5206\u6563\u77E5\u8BC6\uFF0C<strong>\u5B83\u4E0D\u4EE5\u4EFB\u4F55\u5F62\u5F0F\u88AB\u8BB0\u5F55\uFF0C\u53EA\u5B58\u5728\u4E8E\u4E2A\u522B\u4E2A\u4F53\u5FC3\u4E2D</strong>\uFF0C\u4F46\u4F60\u53D1\u73B0\u8FD9\u6837\u7684\u77E5\u8BC6\u5C31\u53EF\u4EE5\u5229\u7528\u5176\u505A\u51FA\u6709\u76CA\u51B3\u7B56\u2014\u2014\u5F00\u4E00\u4E2A\u6C34\u679C\u5E97\u3002</li></ul></li></ul><h3 id="_2-\u91CD\u8981\u6027\u77E5\u8BC6" tabindex="-1"><a class="header-anchor" href="#_2-\u91CD\u8981\u6027\u77E5\u8BC6" aria-hidden="true">#</a> 2. \u91CD\u8981\u6027\u77E5\u8BC6</h3><ul><li><span style="color:#FF8C00;"><u>\u53EA\u77E5\u9053\u5B9A\u6027\u7684\u77E5\u8BC6\u5E76\u4E0D\u8DB3\u4EE5\u5E2E\u52A9\u6211\u4EEC\u89E3\u51B3\u96BE\u9898\u3002</u></span>\u6BD4\u5982\u524D\u8A00\u4E2D<a href="#Story">\u706B\u836F\u914D\u65B9</a>\u7684\u6545\u4E8B\uFF0C\u77E5\u9053\u706B\u836F\u7EC4\u6210\u7684\u5B9A\u6027\u77E5\u8BC6\u5E76\u4E0D\u8DB3\u4EE5\u6210\u529F\u5236\u4F5C\u51FA\u706B\u836F\u3002 <ul><li>\u5F53\u4ECA\u793E\u4F1A\u4E2D\u56FD\u8FD9\u6837\u7684\u4F8B\u5B50\u5F88\u591A\uFF0C\u201D\u6210\u529F\u5B66\u201C\u7684\u4E66\u7C4D\u6587\u7AE0\u5927\u591A\u53EA\u8BB2\u8FF0\u706B\u836F\u7684\u91CD\u8981\u6027\uFF0C\u7136\u540E\u901A\u8FC7\u4E00\u7CFB\u5217\u6545\u4E8B\u548C\u6848\u4F8B\u63CF\u7ED8\u706B\u836F\u7684\u5A01\u529B\uFF0C\u66F4\u4E13\u4E1A\u8005\u4F1A\u89E3\u8BFB\u706B\u836F\u7684\u90E8\u5206\u539F\u7406\uFF0C\u751A\u81F3\u7ED9\u51FA\u706B\u836F\u7684\u5B9A\u6027\u914D\u65B9\uFF0C<br> \u4F46\u662F<strong>\u552F\u72EC\u7F3A\u5C11\u5173\u4E8E\u706B\u836F\u914D\u65B9\u7684\u5B9A\u91CF\u77E5\u8BC6\u3002</strong></li><li>\u5F53\u7136\u8FD9\u672A\u5FC5\u662F\u4F5C\u8005\u6545\u610F\u4E3A\u4E4B\uFF0C\u800C\u662F\u5927\u6982\u7387\u4ED6\u4EEC\u4E5F\u4E0D\u77E5\u9053\uFF0C<span style="color:#FF8C00;"><u>\u7CBE\u786E\u7684\u5B9A\u91CF\u77E5\u8BC6\u9700\u8981\u5DE8\u989D\u6210\u672C\u3002</u></span><br> \u6781\u5C11\u6570\u5E78\u8FD0\u513F\u78B0\u5DE7\u53D1\u73B0\u7CBE\u786E\u7684\u5B9A\u91CF\u77E5\u8BC6\u800C\u53D6\u5F97\u6210\u529F\uFF0C\u5728\u5206\u4EAB\u7ECF\u9A8C\u65F6\u5F80\u5F80\u5FFD\u7565\u5B9A\u91CF\u7684\u90E8\u5206\uFF0C<br> \u6240\u4EE5\u5373\u4FBF\u77E5\u6653\u9053\u7406\u4E5F\u65E0\u6CD5\u5B9E\u8DF5\uFF0C<strong>\u77E5\u6613\u884C\u96BE</strong>\u3002<br> \u4E8B\u52A1\u53C8\u65E0\u65F6\u65E0\u523B\u4E0D\u5904\u4E8E\u53D8\u5316\u53D1\u5C55\u4E4B\u4E2D\uFF0C\u90A3\u4E48\u5B9A\u91CF\u77E5\u8BC6\u81EA\u7136\u968F\u4E4B\u6539\u53D8\u3002</li><li>\u91CD\u8981\u6027\u77E5\u8BC6\u7684\u5FAE\u5F31\u533A\u522B\u5BF9\u9009\u62E9\u5374\u662F\u81F3\u5173\u91CD\u8981\u7684 <ul><li>\u4F8B\u5982\u4E24\u4E2A\u4EBA\u53EF\u80FD\u638C\u63E1\u76F8\u4F3C\u7684\u4FE1\u606F\u2014\u2014A\u6BD4B\u91CD\u8981\uFF0C\u4F46\u662F\u6BD4\u4F8B\u4E0D\u540C\uFF0C\u4E00\u4E2A\u662F7:3\u4E00\u4E2A\u662F8:2\u3002\u6700\u540E\u51B3\u7B56\u65F6\u5C31\u4F1A\u4EA7\u751F\u533A\u522B\uFF0C\u800C\u201D\u5931\u4E4B\u6BEB\u5398\uFF0C\u8C2C\u4E4B\u5343\u91CC\u201C\u53E4\u4ECA\u4E2D\u5916\u5411\u6765\u662F\u901A\u7528\u7684\uFF1B</li><li>\u5C24\u5176\u662F\u4E92\u8054\u7F51\u65F6\u4EE3\uFF0C\u80DC\u8005\u4E3A\u738B\u3001\u8D62\u5BB6\u901A\u5403\u7684\u60C5\u51B5\u5C61\u89C1\u4E0D\u9C9C\uFF0C\u65B9\u5411\u7684\u9009\u62E9\u5E26\u6765\u6700\u7EC8\u7ED3\u679C\u7684\u5DE8\u5927\u5DEE\u5F02\u3002</li></ul></li></ul></li></ul><h3 id="_3-\u5B9E\u8DF5\u77E5\u8BC6" tabindex="-1"><a class="header-anchor" href="#_3-\u5B9E\u8DF5\u77E5\u8BC6" aria-hidden="true">#</a> 3. \u5B9E\u8DF5\u77E5\u8BC6</h3><ul><li>\u201D\u9ED1\u767D\u739B\u4E3D\u201C\u662F\u8457\u540D\u7684\u601D\u60F3\u5B9E\u9A8C\uFF1A<br> \u8BBE\u60F3\u739B\u4E3D\u4ECE\u51FA\u751F\u5F00\u59CB\u5C31\u88AB\u5173\u5728\u6240\u6709\u5E03\u7F6E\u90FD\u662F\u9ED1\u767D\u7684\u623F\u95F4\uFF0C\u739B\u4E3D\u53EF\u4EE5\u901A\u8FC7\u9ED1\u767D\u7535\u89C6\u673A\uFF08\u6216\u4E66\u672C\uFF09\u5B66\u4E60\u5916\u754C\u7684\u77E5\u8BC6\u2014\u2014\u5F53\u7136\u5305\u62EC\u989C\u8272\u7684\u5149\u5B66\u548C\u795E\u7ECF\u5B66\u77E5\u8BC6\u3002<br> \u5047\u8BBE\u739B\u4E3D\u5B66\u4F1A\u5168\u90E8\u7269\u7406\u5B66\u77E5\u8BC6\u540E\u88AB\u91CA\u653E\u5230\u771F\u5B9E\u4E16\u754C\uFF0C\u5F53\u770B\u5230\u771F\u6B63\u7684\u897F\u7EA2\u67FF\u65F6\u4F1A\u6709\u4EC0\u4E48\u611F\u53D7\uFF1F</li><li>\u54F2\u5B66\u5BB6 Jackson \u8BA4\u4E3A<br><blockquote><p>\u6839\u636E\u6211\u4EEC\u7684\u76F4\u89C9\uFF0C\u4E00\u822C\u4F1A\u8BA4\u4E3A\u739B\u4E3D\u5C06\u83B7\u5F97\u67D0\u79CD\u5168\u65B0\u7684\u611F\u53D7\uFF08sensation\uFF09</p></blockquote></li></ul><hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="footnote1" class="footnote-item"><p>\u8BE5\u6587\u662F2011\u5E74\u300A\u7F8E\u56FD\u7ECF\u6D4E\u8BC4\u8BBA\u300B\u521B\u520A100\u5468\u5E74\u53D1\u8868\u8FC7\u768420\u7BC7\u6700\u7ECF\u5178\u6587\u7AE0\u4E4B\u4E00\u3002 <a href="#footnote-ref1" class="footnote-backref">\u21A9\uFE0E</a></p></li></ol></section>',12);function g(k,m){const t=i("ExternalLinkIcon");return n(),s("div",null,[o("p",null,[d,o("a",h,[u,r(t)]),b,o("a",f,[_,r(t)])]),p])}var y=a(c,[["render",g],["__file","DarkKnowledge.html.vue"]]);export{y as default};
